import React, { useState } from 'react'

const List = () => {

    const initialState = [
        {
            task: 'サンプルテキスト１',
            isCompleted: false
        },
        {
            task: 'サンプルテキスト２',
            isCompleted: false
        },
        {
            task: 'サンプルテキスト３',
            isCompleted: false
        },     
    ]

    const [todos, setTodo] = useState(initialState); 
    //useStateにinitialStateを指定し、todosにTodoリストを保存。todosの更新にはsetTodoを利用。

    const [task, setTask] = useState('')

    const handleNewTask = (event) => {
        setTask( event.target.value )
    }

    const handleRemoveTask = index => {
        const newTodos = [...todos]
        newTodos.splice(index,1)
        setTodo(newTodos)
    }

    return (
        <ul>
        { todos.map((todo, index) => (  //ToDoリストを画面上に表示するため、Todoリストが保存されているtodosをmap関数で展開。
        <li key={ index }>
        <span onClick={ () =>handleRemoveTask(index) }>
        ☑️
        </span>
        { todo.task }
        </li>
        ))}
        </ul>
    )
}

export default List
