        setTask( event.target.value )
    }

    const handleSubmit = (event) => {
        event.preventDefault()
        if(task === '') return
        setTodo(todos => [...todos,{ task, isCompleted: false}])
        setTask('')
    }


    return (
        <div class="wrap">
            <div class="inner">
                <h1>Todo App</h1>
                <form onSubmit={ handleSubmit }>
                <input placeholder="ここにTodoを入力" onChange={handleNewTask}/></form>
                <List />
            </div>
        </div>
  );
}

export default Form
